// Documented in superclass.
void
SoWinRenderArea::afterRealizeHook(void)
{
  inherited::afterRealizeHook();

#ifdef HAVE_JOYSTICK_LINUX
  if (SoWinLinuxJoystick::exists())
    this->registerDevice(new SoWinLinuxJoystick);
#endif // HAVE_JOYSTICK_LINUX

#ifdef HAVE_SPACENAV_SUPPORT
  PRIVATE(this)->devices.spacenav = new SoWinSpacenavDevice;
  this->registerDevice(PRIVATE(this)->devices.spacenav);
#endif // HAVE_SPACENAV_SUPPORT
}

